--查看各表select* from Categoryselect* from Wareselect* from Employeeselect* from Salesinfo--向商品类别表中添加信息insert into Category(Cname,Description)values('食品类','速食面/饼干/火腿肠/零散食品等')insert into Category(Cname,Description)values('日化用品','毛巾/牙刷/手纸/洗衣粉/肥皂等')insert into Category(Cname,Description)values('服装类','女装/男装/童装/运动装等')insert into Category(Cname,Description)values('家电类','洗衣机/电视机/空调/电冰箱等')insert into Category(Cname,Description)values('玩具类','拼图玩具/游戏玩具类')--向商品信息表中添加信息--1.食品类insert into Ware values('康师傅',1,39,48,250)insert into Ware values('统一大骨面',1,32,40,250)insert into Ware values('老北京',1,36,45,250)insert into Ware values('魔法士干吃面',1,24,32,250)insert into Ware values('火鸡面',1,19,25,250)--2.日化用品类insert into Ware values('洁丽雅毛巾',2,6,12,250)insert into Ware values('南极人毛巾',2,8,16,250)insert into Ware values('吸水毛巾',2,5,10,250)insert into Ware values('牙刷',2,2,4,250)insert into Ware values('电动牙刷',2,45,65,250)--3.服装类insert into Ware values('太平鸟（男裤区）',3,239,368,250)insert into Ware values('太平鸟（棉衣区）',3,479,587,250)insert into Ware values('太平鸟（女外套区）',3,239,478,250)insert into Ware values('太平鸟（女毛衣区）',3,126,248,250)insert into Ware values('太平鸟（女裤区）',3,188,368,250)--4.家电类insert into Ware values('海尔全自动洗衣机',4,2849,5628,250)insert into Ware values('美的全自动洗衣机',4,2124,4288,250)insert into Ware values('三洋全自动洗衣机',4,2018,4036,250)insert into Ware values('夏普电视机',4,4036,5068,250)insert into Ware values('海信电视机',4,3160,4320,250)--5.玩具类insert into Ware values('迷你台球',5,39,68,250)insert into Ware values('室内篮球',5,60,140,250)insert into Ware values('足球桌',5,78,158,250)insert into Ware values('迷你台球',5,39,68,250)insert into Ware values('米白色一米八大熊',5,269,556,250)insert into Ware values('芭比娃娃爱丽丝',5,238,576,250)--向员工表中添加信息insert into Employee values('王长虎','1111111','我是员工我怕谁')insert into Employee values('邢聪','1111111','我是员工我怕谁')insert into Employee values('王军凯','1111111','我是员工我怕谁')--向销售记录表中添加信息select*from Salesinfoupdate Ware set Amount=Amount-1 where Wname='牙刷'insert into Salesinfo values(9,'2017-11-29',1,1001)update Ware set Amount=Amount-1 where Wname='统一大骨面'insert into Salesinfo values(2,'2017-11-29',1,1001)update Ware set Amount=Amount-50 where Wname='老北京'insert into Salesinfo values(3,'2017-11-29',50,1002)--查询显示每件商品的编号、名称以及利润，按利润从高到低进行排序--每件商品的利润需通过(售价-进价)计算而来，ORDER BY后面的表达式也应该是(售价-进价)select Wid,Wname,SalesPrice-PurchasePrice as 利润 from Wareorder by SalesPrice-PurchasePrice desc--统计每件商品的销售量，显示商品名称及销售量，按销售量从高到低排序--商品名称在Ware表中，销售量在SalesInfo表中，所以首先需在这两个表中进行连接查询，然后再分组统计select Wname,sum(SalesAmount) as 销售量 from Ware w,SalesInfo s
where w.Wid=s.Wid group by Wname order by sum(SalesAmount) desc
--统计每类商品的销售量，显示商品类别及销售量--该问题涉及到Category、Ware、SalesInfo三个表，先连接再分组统计select Cname,sum(SalesAmount) as 销售量 from Category c,Ware w,SalesInfo swhere c.Cid=w.Cid and w.Wid=s.Wid group by Cname--统计每种商品到目前的盈利总额--先连接Ware和SalesInfo表，从中得到商品名称、进价、售价及销售数量，--结果中每条记录的销售额应表示为： SalesPrice-PurchasePrice)*SalesAmount--最后分组统计select Wname,sum((SalesPrice-PurchasePrice)*SalesAmount) as 盈利总额from Ware w,SalesInfo swhere w.Wid=s.Wid group by Wname